<?php
/**
 * All Modules.
 *
 * @since 2.0.0
 */

if ( ! defined( 'ABSPATH' ) ) {
	exit;
}

$all_modules = Merchant_Admin_Modules::get_modules();
if ( empty( $all_modules ) ) {
	return;
}
?>
<?php foreach ( $all_modules as $module_group ) : ?>
	<div class="merchant-modules-box">
		<div class="merchant-modules-box-heading">
			<?php echo esc_html( $module_group['title'] ?? '' ); ?>
		</div>

		<?php if ( ! empty( $module_group['modules'] ) ) : ?>
			<div class="merchant-modules-list">
				<?php
				foreach ( $module_group['modules'] as $module_id => $module_data ) :
					$is_pro      = ! defined( 'MERCHANT_PRO_VERSION' ) && ( $module_data['pro'] ?? false ) === true;
					$module_link = add_query_arg( array( 'page' => 'merchant', 'module' => $module_id ), 'admin.php' );
					$link_target = '_self';

					/**
					 * Hook 'merchant_admin_module_{module_id}_list_item_class'
					 *
					 * @since 1.0
					 */
					$module_class = apply_filters( "merchant_admin_module_{$module_id}_list_item_class", 'merchant-modules-list-item' );

					$requires_wc_class = strpos( $module_class, 'merchant-module-wc-only' ) !== false;
					$deactivated_by_bp = strpos( $module_class, 'merchant-module-deactivated-by-bp' ) !== false;

					if ( $requires_wc_class ) {
						if ( $is_pro ) {
							$module_link = 'https://athemes.com/merchant-upgrade?utm_source=plugin_dashboard&utm_content='. $module_id .'&utm_medium=merchant_dashboard&utm_campaign=Merchant';
							$link_target = '_blank';
						} else {
							$module_link = '#';
						}
					}
					?>
					<a href="<?php echo esc_url( $module_link ); ?>" class="<?php echo esc_attr( $module_class ) ?>" target="<?php echo esc_attr( $link_target ); ?>" data-module-id="<?php echo esc_attr( $module_id ); ?>">
						<?php if ( $deactivated_by_bp ) : ?>
							<div class="merchant-modules-list-item-badge-wrapper merchant-modules-list-item-removed-by-bp">
                                <span class="merchant-pro-badge merchant-pro-tooltip" data-tooltip-message="<?php echo esc_attr__( 'Botiga module is taking precedence', 'merchant' ); ?>">
                                    <?php echo esc_html__( 'In use', 'merchant' ); ?>
                                </span>
							</div>
						<?php endif; ?>

						<?php if ( $requires_wc_class ) : ?>
							<div class="merchant-modules-list-item-badge-wrapper merchant-modules-list-item-woo-required">
                                <span class="merchant-pro-badge merchant-pro-tooltip" data-tooltip-message="<?php echo esc_attr__( 'The WooCommerce plugin is required for this option', 'merchant' ); ?>">
                                    <?php echo esc_html__( 'WOO', 'merchant' ); ?>
                                </span>
							</div>
						<?php endif; ?>

